#include <bits/stdc++.h>
using namespace std;
typedef long long ll;

ll n, b;
vector<ll> fac,num;//facnum

int main()
{
    cin>>n>>b;
    for(ll i=2;i*i<=b;i++)		//b
    {
        if(b%i==0)
        {
            ll cnt=0;
            while(b%i==0)
            {
                b/=i;
                cnt++;
            }
            fac.push_back(i);
			num.push_back(cnt);
        }
    }

    if(b>1)		//
	{
		fac.push_back(b);
		num.push_back(1);
	}

    ll ans=0x3f3f3f3f3f3f3f3f;
    for (int i=0;i<fac.size();++i)//
    {
        ll cnt=0;
        ll t=n;
        while(t)	
        {
            cnt+=t/fac[i];	// t fac[i] 
            t/=fac[i];		// fac[i]   fac[i] 
        }
        cnt/=num[i];	//b
        ans=min(ans,cnt);	//
    }
    if(fac.empty())		//
        cout<<0<<endl;
    else
        cout<<ans<<endl;
    return 0;
}

