#include<bits/stdc++.h>
using namespace std;
const int N=3e5;
int n;
//v[i]ip[i]ivis[i]
int v[N],p[N],vis[N];
//mp[i]pn[i]i
vector<int> mp[N],pn[N];

void getp()//
{
    for(int i=2;i<N;++i)
    {
        if(!p[i])
        {
            for(int j=i;j<N;j+=i)
            {
                if(!p[j])
                    p[j]=i;
            }
        }
    }
}
pair<int,int> dfs(int np,int nx,int d,int f)//DFS,pair<>
{
    int mx=nx,md=d;
    pair<int,int> re;
    for(int i=0;i<mp[nx].size();++i)
    {
        int x=mp[nx][i];
        if(vis[x]==np&&x!=f)
        {
            re=dfs(np,x,d+1,nx);
            if(re.second>md)
            {
                md=re.second;
                mx=re.first;
            }
        }
    }
    return make_pair(mx,md);
}
int longest(int np,int nx)//
{
    int t=dfs(np,nx,1,-1).first;//
    return dfs(np,t,1,-1).second;//
}
int bfs(int np,int nx)
{
    queue<int> q;
    q.push(nx);
    vis[nx]=np;
    while(!q.empty())//BFSnpxvis[x]=np
    {
        int now=q.front();q.pop();
        for(int i=0;i<mp[now].size();++i)
        {
            int x=mp[now][i];
            if(vis[x]!=np&&!(v[x]%np))
            {
                q.push(x);
                vis[x]=np;
            }
        }
    }
    return longest(np,nx);//
}
int main()
{
    cin>>n;
    for(int i=1;i<=n;++i)
        scanf("%d",&v[i]);
    int u,w;
    for(int i=1;i<n;++i)
    {
        scanf("%d%d",&u,&w);
        mp[u].push_back(w);
        mp[w].push_back(u);
    }
    getp();//
    for(int i=1;i<=n;++i)
    {
        int x=v[i];
        while(x!=1)
        {
            int y=p[x];
            pn[y].push_back(i);//yyi
            while(!(x%y))
                x/=y;
        }
