    int ans=0;
    for(int i=2;i<N;++i)//
    {
        if(pn[i].size()>0)//
        {
            for(int j=0;j<pn[i].size();++j)//
            {
                int x=pn[i][j];
                if(vis[x]!=i)
                    ans=max(ans,bfs(i,x));//
            }
        }
    }
    printf("%d\n",ans);
}

