#include<cstdio>
#include<cstring>
using namespace std;
const int N =1e6+7;
char s[N];
int next[N];
int len;
void getnext()
{
	memset(next,0,sizeof(next));
	int j=0,k=-1;
	next[0]=-1;
	while(j<len)
	{
		while(k!=-1&&s[j]!=s[k])
			k=next[k];
		++j;++k;
		next[j]=k;
	}
}

int main()
{
	while(~scanf("%s",s))
	{
		if(s[0]=='.')
			break;
		len=strlen(s);
		getnext();
		int l=len-next[len];
		if(len%l==0)
			printf("%d\n",len/l);
		else
			puts("1");
	}
}

