#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
const ll mod=1e9+7;
ll factor[200],findex[200];//factor[i]findex[i]
ll dp[100][10005],inv[1000];//dp[i][j]ji
ll n,k,cnt;//cnt
ll ans;
void getinv() //
{
	inv[1]=1;
	for(int i=2;i<1000;i++)
		inv[i]=(mod-mod/i)*inv[mod%i]%mod;
}
ll DP(int now)
{
	memset(dp,0,sizeof(dp));
	dp[findex[now]][0]=1;//,findex[now]
	for(int i=1;i<=k;++i)//k
	{
		for(int j=0;j<=findex[now];++j)//(i-1)j
		{
			if(dp[j][i-1])//(i-1)j
			{
				for(int l=0;l<=j;++l)//i<=j
					dp[l][i]=( dp[l][i] + (dp[j][i-1]*inv[j+1]%mod) )%mod;
			}
		}
	}
	ll t=1,ret=0;
	for(int i=0;i<=findex[now];++i)//
	{
		ret=(ret+t*dp[i][k])%mod;
		t=t*factor[now]%mod;
	}
	return ret;
}
int main()
{
	getinv();
	cin>>n>>k;
	for(int i=2;i<=sqrt(n);++i)//
	{
		if(n%i==0)
		{
			factor[++cnt]=i;
			while(n%i==0)
			{
				n/=i;
				++findex[cnt];//
			}
		}
	}
	if(n>1)
	{
		factor[++cnt]=n;
		findex[cnt]=1;
	}
	ans=1;
	for(int i=1;i<=cnt;++i)//DP
		ans=ans*DP(i)%mod;
	cout<<ans<<endl;
}

